import React, { useState, useEffect } from "react";
import axios from "axios"; // Import axios
import "./OrderStatus.css";

const OrderStatus = () => {
  const [status, setStatus] = useState("All");
  const [orders, setOrders] = useState([]); // State to hold fetched orders

  // Function to fetch orders from the backend
  const fetchOrders = async () => {
    try {
      const response = await axios.get(status === "All" ? "/all-orders" : `/${status}-orders`);
      setOrders(response.data[`${status} orders`] || response.data["All order status"]);
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  useEffect(() => {
    fetchOrders(); // Fetch orders when the component mounts or status changes
  }, [status]);

  const handleDropdownChange = (e) => {
    setStatus(e.target.value);
  };

  return (
    <div>
      {/* Display Selected Status and align it to the left */}
      <div className="header-container">
        <h3 className="order-status-header">Selected Status: {status}</h3>
        {/* Select Status Label and Dropdown */}
        <div className="dropdown-container">
          <select
            id="status-select"
            className="dropdown"
            value={status}
            onChange={handleDropdownChange}
          >
            <option value="All">All</option>
            <option value="Shipped">Shipped</option>
            <option value="To Shipped">To Shipped</option>
            <option value="Received">Received</option>
          </select>
        </div>
      </div>

      // Render filtered cards
{orders.map(order => (
  <div key={order.id} className="card">
    <img src="https://via.placeholder.com/60" alt="Product" />
    <div className="card-details">
      <p className="product-name">{order["Product Name"]}</p>
      <p>Size: {order.Size}</p>
      <p>Quantity: {order.Quantity}</p>
      <p>Price: {order["Total Price"]}</p>
      <p className={`order-status ${order.status ? order.status.toLowerCase().replace(' ', '-') : 'unknown-status'}`}>
        Status: {order.status || 'Unknown'}
      </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default OrderStatus;
