import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OrderForm.css';
import AddNewAddressModal from './AddNewAddressModal';

const OrderForm = () => {
    const [products, setProducts] = useState([]);
    const [sizes, setSizes] = useState([]);
    const [deliveryAddress, setDeliveryAddress] = useState('');
    const [recipientName, setRecipientName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [email, setEmail] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [selectedProduct, setSelectedProduct] = useState('');
    const [selectedSize, setSelectedSize] = useState('');
    const [imageSrc, setImageSrc] = useState('../assets/IMS_FINAL_LOGO.jpg');
    const [showAddNewAddressModal, setShowAddNewAddressModal] = useState(false);

    useEffect(() => {
        fetchProducts();
        fetchWarehouseAddress();
    }, []);

    // Fetch product list from backend
    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:8000/ims/products');
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
            alert("Failed to load products.");
        }
    };

    // Fetch warehouse address
    const fetchWarehouseAddress = async () => {
        try {
            const response = await axios.get("http://127.0.0.1:8000/warehouse-address");
            setDeliveryAddress(response.data.address);
        } catch (error) {
            console.error("Error fetching warehouse address:", error);
        }
    };

    // Fetch sizes when product is selected
    const fetchSizes = async (product) => {
        try {
            const response = await axios.get(`http://127.0.0.1:8000/ims/products/sizes?productName=${product}`);
            setSizes(response.data);
        } catch (error) {
            console.error("Error fetching sizes:", error);
        }
    };

    // Handle product selection and update image
    const handleProductChange = (e) => {
        const productName = e.target.value;
        setSelectedProduct(productName);
        fetchSizes(productName);

        const product = products.find(p => p.productName === productName);
        setImageSrc(product ? product.image_path : '/path/to/placeholder-image.jpg');
    };

    // Handle order submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("access_token");
        if (!token) {
            alert("Unauthorized: No access token found.");
            return;
        }

        const orderData = {
            supplier_id: 1,
            products: [{ productName: selectedProduct, size: selectedSize, quantity }],
            total_price: quantity * (products.find(p => p.productName === selectedProduct)?.unitPrice || 0),
            delivery_address: deliveryAddress,
        };

        try {
            const response = await axios.post("http://127.0.0.1:8000/purchase-order/create-purchase-order", orderData, {
                headers: { Authorization: `Bearer ${token}` }
            });
            alert(response.data.message);
        } catch (error) {
            alert("Order failed: " + (error.response?.data?.detail || "Unknown error"));
        }
    };

    return (
        <div className="order-form-container">
            <div className="order-form">
                <h2 className="form-title">Order Form</h2>

                {/* Product Image */}
                <div className="product-image-container">
                    <img src={imageSrc} alt="Product" className="product-image" />
                </div>

                {/* Product Dropdown */}
                <div className="form-group">
                    <label>Product Name</label>
                    <select value={selectedProduct} onChange={handleProductChange}>
                        <option value="">Select Product</option>
                        {products.map((product) => (
                            <option key={product.productName} value={product.productName}>
                                {product.productName}
                            </option>
                        ))}
                    </select>
                </div>

                {/* Product Size */}
                <div className="form-group">
                    <label>Product Size</label>
                    <select value={selectedSize} onChange={(e) => setSelectedSize(e.target.value)}>
                        <option value="">Select Size</option>
                        {sizes.map((size) => (
                            <option key={size.size} value={size.size}>{size.size}</option>
                        ))}
                    </select>
                </div>

                {/* Quantity */}
                <div className="form-group">
                    <label>Quantity</label>
                    <input type="number" value={quantity} onChange={(e) => setQuantity(Math.max(1, e.target.value))} min="1" />
                </div>

                {/* Delivery Address */}
                <div className="form-group">
                    <label>Delivery Address</label>
                    <select value={deliveryAddress} onChange={(e) => setDeliveryAddress(e.target.value)}>
                        <option value="">Select Address</option>
                        <option value={deliveryAddress}>{deliveryAddress}</option>
                        <option value="add_new">Add New Address</option>
                    </select>
                    {deliveryAddress === 'add_new' && (
                        <button type="button" className="add-new-address-btn" onClick={() => setShowAddNewAddressModal(true)}>
                            Add New Address
                        </button>
                    )}
                </div>

                {/* Recipient Details */}
                <div className="form-group">
                    <label>Recipient Name</label>
                    <input type="text" value={recipientName} onChange={(e) => setRecipientName(e.target.value)} placeholder="Enter Recipient Name" />
                </div>

                <div className="form-group">
                    <label>Phone Number</label>
                    <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Enter Phone Number" />
                </div>

                <div className="form-group">
                    <label>Email Address</label>
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter Email Address" />
                </div>

                {/* Submit Button */}
                <button type="submit" className="submit-btn" onClick={handleSubmit}>Submit Order</button>
            </div>

            {/* Add New Address Modal */}
            {showAddNewAddressModal && <AddNewAddressModal onClose={() => setShowAddNewAddressModal(false)} />}
        </div>
    );
};

export default OrderForm;
