import React, { useEffect, useState } from "react";
import axios from "axios";
import "./OrderStatus.css";

const OrderStatus = () => {
  const [status, setStatus] = useState("All");
  const [orders, setOrders] = useState([]); // state to hold fetched orders

  //function to fetch orders from the backend
  const fetchOrders = async () => {
    try {
      const response = await axios.get(status === "All" ? "/receive-orders/all-orders" : `/receive-orders/${status}-orders`);
      setOrders(response.data[`${status} orders`] || response.data["All order status"]);
    } catch (error) {
      console.error("Error fetching orders: ", error);
    }
  };

  useEffect(() => {
    fetchOrders(); // fetch orders when the component mounts or status changes
  }, [status]);

  const handleDropdownChange = (e) => {
    setStatus(e.target.value);
  };

  return (
    <div>
      {/* Display Selected Status and align it to the left */}
      <div className="header-container">
        <h3 className="order-status-header">Selected Status: {status}</h3>
        {/* Select Status Label and Dropdown */}
        <div className="dropdown-container">
          <select
            id="status-select"
            className="dropdown"
            value={status}
            onChange={handleDropdownChange}
          >
            <option value="All">All</option>
            <option value="Pending">Pending</option>
            <option value="Confirmed">Confirmed</option>
            <option value="Rejected">Rejected</option>
            <option value="To Ship">To Ship</option>
            <option value="Delivered">Received</option>
          </select>
        </div>
      </div>

      {/* Render filtered cards */}
      {orders.map(order => (
        <div key={order.id} className="card">
          <img src="https://via.placeholder.com/60" alt="Product" />
          <div className="card-details">
            <p className="product-name">{order["Product Name"]}</p>
            <p className="category">{order.Category}</p>
            <p>Size: {order.Size}</p>
            <p>Quantity: {order.Quantity}</p>
            <p>Total Price: {order["Total Price"]}</p>
            <p className={`order-status ${order.status.toLowerCase().replace(' ', '-')}`}>
              Status: {order.status}
            <p className="date">Date: {order.Date}</p>
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default OrderStatus;
