import React, { useState } from 'react';
import { jwtDecode } from 'jwt-decode';  // Correct import statement
import './Login.css';

const Login = ({ onLogin, onRoleSelect }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/auth/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',  // Change this to application/json
        },
        body: JSON.stringify({
          username: username,
          password: password,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        const { access_token } = data;

        // Store token in localStorage
        localStorage.setItem('access_token', access_token);

        // Decode the token and extract the role
        const decodedToken = jwt_decode(access_token);
        const role = decodedToken.role;

        // Call the onLogin callback with the role
        onLogin(role);
        onRoleSelect(role);  // Optional if you need the role explicitly
      } else {
        alert('Invalid username or password');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while logging in.');
    }
  };

  return (
    <div className="login-container">
      <div className="blur-bg"></div>
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Welcome Back</h2>
        <p className="login-description">Please log in to your account</p>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="login-btn">
          Log In
        </button>
      </form>
    </div>
  );
};

export default Login;
