import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Sales.css"; 

const Sales = () => {
  // State to hold the sales data fetched from the backend
  const [salesData, setSalesData] = useState([]);
  const [error, setError] = userState(null);

  // // fetch sales data from the backend
  // useEffect(() => {
  //   const fetchSalesData = async () => {
  //     const token = localStorage.getItem("access_token");
  //     if (!token) {
  //       setError("Unauthorize: No access token found");
  //       return;
  //     }

  //     try
  //     {
  //       const response = await axios.get("/")
  //     }
  //   }
  // })

  return (
    <div className="sales-page">
      {/* Sales header */}
      <h1>Sales</h1> {/* Added header for the Sales page */}

      {/* Table Placeholder for Backend Data */}
      <div className="table-container">
        <table className="sales-table">
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Category</th>
              <th>Size</th>
              <th>Total Quantity Sold</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {/* Placeholder rows */}
            {salesData.length === 0 ? (
              <tr>
                <td colSpan="4" className="placeholder">
                  Loading data...
                </td>
              </tr>
            ) : (
              salesData.map((sale, index) => (
                <tr key={index}>
                  <td>{sale.productName}</td>
                  <td>{sale.category}</td>
                  <td>{sale.size}</td>
                  <td>{sale.totalQuantity}</td>
                  <td>{sale.date}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Sales;
